import { FORM_PROJECT, OBTAIN_PROJECTS, ADD_PROJECT, VALIDATE_FORM} from '../../types'

export default (state, action) => {
    switch(action.type) {
        case FORM_PROJECT:
            return {
                ...state,
                form: true
            }
        case OBTAIN_PROJECTS :
            return {
                ...state,
                projects: action.payload    
            }
        case ADD_PROJECT :
            return{
                ...state,
                projects: [...state.projects, action.payload],
                form: false,
                errorForm: false
            }
        case VALIDATE_FORM : 
            return{
                ...state,
                errorForm: true
            }
        default:
            return state;
    }
}
