import { TASKS_PROJECT, ADD_TASK, VALIDATE_TASK } from '../../types/index'


export default (state, action) => {
    switch(action.type) {
        case TASKS_PROJECT : 
        return {
            ...state,
            tasksproject: state.tasks.filter(task => task.projectId === action.payload)
        }
        case ADD_TASK :
            return {
                ...state,
                tasks: [...state.tasks, action.payload]
            }
        case VALIDATE_TASK : 
            return {
                ...state,
                errortask: true
            }
        default:
            return state;
    }
}
